function ejemploFiltroPromedio  % Ejemplo    pkg load image;  clear;  clc;  close all;    filtroPromedio(3);  endfunctionfunction filtroPromedio(k)  % Esta funcion aplica el filtro promedio a una imagen  % en escala de grises  % Parametros de entrada: k = ajusta la  %                        difuminacion de la imagen    A = imread('../../Imagenes/child.jpg');  subplot(1, 2, 1);  imshow(A);  title('Imagen Original');  A = im2double(A);  B = (1 / k^2) * ones(k);  C = conv2(A, B, 'same');  C = im2uint8(C);    subplot(1, 2, 2);  imshow(C);  title('Imagen Modificada');  endfunction